class Countdown extends Component {
  constructor(props) {
    super(props);
    this.state = { count: props.count };
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState(
        prevState =>
          prevState.count > 0 ? { count: prevState.count - 1 } : prevState,
      );
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    return (

        {this.state.count}

    );
  }
}

